import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const useQuery = () => {
  return new URLSearchParams(useLocation().search);
};

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const query = useQuery();
  const category = query.get('category');

  useEffect(() => {
    fetch(`http://127.0.0.1:5000/api/books?category=${encodeURIComponent(category)}`)
      .then(response => response.json())
      .then(data => {
        setBooks(data);
      })
      .catch(error => console.error('Error fetching data: ', error));
  }, [category]);

  const cellStyle = {
    padding: '10px',
    lineHeight: 'normal',
  };

  const rowStyle = {
    borderBottom: '1px solid gray', // Adds a line between rows
  };

  const contentCellStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between', // Aligns content to the top and bottom
    height: '150px', // Adjust as needed
  };

  return (
    <div style={{ margin: '0 auto', maxWidth: '1170px', padding: '16px' }}>
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        border: '1px solid gray',
      }}>
        <thead>
          <tr style={rowStyle}>
            <th style={cellStyle}>Cover</th>
            <th style={cellStyle}>Title</th>
            {/* Add other headers here */}
          </tr>
        </thead>
        <tbody>
          {books.map((book, index) => (
            <tr key={book.id} style={rowStyle}>
              <td style={cellStyle}>
                <div style={{ textAlign: 'center' }}>
                  <img
                    src={book.img_url} // Make sure your book objects have an img_url field
                    alt={book.title}
                    style={{ maxWidth: '100px', height: 'auto', margin: '0 auto' }}
                  />
                </div>
              </td>
              <td style={{ ...cellStyle, ...contentCellStyle }}>
                <span style={{ fontSize: '26px'}}>{book.title}</span>
                {/* Display other book details here if needed */}
              </td>
              {/* Add other cells here */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BooksPage;
