import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const query = useQuery();
  const categoryName = query.get('category');

  useEffect(() => {
    if (!categoryName) {
      setIsLoading(false);
      setError('No category specified');
      return;
    }

    setIsLoading(true);
    fetch(`http://127.0.0.1:5000/api/images?category=${encodeURIComponent(categoryName)}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`Network response was not ok, status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setBooks(data); // Assuming the API returns an array of books/images
        setIsLoading(false);
      })
      .catch(err => {
        console.error('Error fetching books:', err);
        setError(err.message);
        setIsLoading(false);
      });
  }, [categoryName]); // Dependency on categoryName

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Styles
  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    border: '1px solid gray',
    marginTop: '20px',
  };
  const rowStyle = {
    borderBottom: '1px solid gray',
  };
  const imageCellStyle = {
    width: '120px',
    textAlign: 'center',
    verticalAlign: 'top',
    borderRight: '1px solid gray',
  };
  const titleCellStyle = {
    verticalAlign: 'top',
    padding: '10px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    height: '150px', // Adjust based on your content or image height
  };
  const detailCellStyle = {
    padding: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-end',
    borderTop: '1px solid gray', // Separate details from the title/publisher/authors
    height: '60px', // Adjust the height to align details at the bottom of the cell
  };
  const detailSpanStyle = {
    marginRight: '10px', // Add some space between the details
    whiteSpace: 'nowrap', // Keep each detail on one line
  };

  return (
    <div>
      <h1>Books in Category: {categoryName}</h1>
      <table style={tableStyle}>
        <tbody>
          {books.map((book, index) => (
            <tr key={index} style={rowStyle}>
              <td style={imageCellStyle}>
                <img
                  src={book.img_url} // Ensure each book has an img_url property
                  alt={book.title}
                  style={{ maxWidth: '100px', height: 'auto', display: 'block' }}
                />
              </td>
              <td style={titleCellStyle}>
                <div>
                  <strong>{book.title || 'Untitled'}</strong>
                  <div>{book.publisher || 'Unknown Publisher'}</div>
                  <div>{book.authors || 'Unknown Authors'}</div>
                </div>
                <div style={detailCellStyle}>
                  <span style={detailSpanStyle}>Year: {book.year || 'Unknown'}</span>
                  <span style={detailSpanStyle}>Language: {book.language || 'Unknown'}</span>
                  <span style={detailSpanStyle}>File: {book.file || 'Unknown'}</span>
                  <span style={detailSpanStyle}>{book.size || 'Unknown Size'}</span>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BooksPage;
