// ... (rest of your imports and component)

const BooksPage = () => {
  // ... (state hooks, useEffect)

  // Styles
  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    border: '1px solid gray',
    marginTop: '20px',
  };
  const rowStyle = {
    borderBottom: '1px solid gray',
  };
  const imageCellStyle = {
    width: '120px',
    textAlign: 'center',
    verticalAlign: 'top',
    borderRight: '1px solid gray',
  };
  const titleCellStyle = {
    verticalAlign: 'top',
    padding: '10px',
  };
  const detailCellStyle = {
    verticalAlign: 'bottom',
    padding: '10px',
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center',
  };

  const detailSpanStyle = {
    marginRight: '10px', // Add some space between the details
    whiteSpace: 'nowrap', // Keep each detail in one line
  };

  // ... (rest of your component)

  return (
    <div>
      <h1>Books in Category: {categoryName}</h1>
      <table style={tableStyle}>
        <tbody>
          {books.map((book, index) => (
            <tr key={index} style={rowStyle}>
              <td style={imageCellStyle}>
                <img
                  src={book.img_url} // Ensure each book has an img_url property
                  alt={book.title}
                  style={{ maxWidth: '100px', height: 'auto', display: 'block' }}
                />
              </td>
              <td style={titleCellStyle}>
                <div><strong>{book.title || 'Untitled'}</strong></div>
                <div>{book.publisher || 'Unknown Publisher'}</div>
                <div>{book.authors || 'Unknown Authors'}</div>
              </td>
              <td style={detailCellStyle}>
                <span style={detailSpanStyle}>Year: {book.year || 'Unknown'}</span>
                <span style={detailSpanStyle}>Language: {book.language || 'Unknown'}</span>
                <span style={detailSpanStyle}>File: {book.file || 'Unknown'}</span>
                <span style={detailSpanStyle}>{book.size || 'Unknown Size'}</span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BooksPage;
