import React, { useState, useEffect } from 'react';
import { Grid, Box, Typography, List, ListItem } from '@mui/material';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const CategoriesPage = () => {
  const [categories, setCategories] = useState({});

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/images')
      .then(response => response.json())
      .then(data => {
        const categoryMap = data.reduce((acc, item) => {
          const { category, subCategory } = item;
          if (category) {
            if (!acc[category]) {
              acc[category] = { subCategories: new Set(), books: [] };
            }
            if (subCategory) {
              acc[category].subCategories.add(subCategory);
            }
            acc[category].books.push(item);
          }
          return acc;
        }, {});

        const sortedCategories = Object.keys(categoryMap).sort().reduce((sortedAcc, key) => ({
          ...sortedAcc,
          [key]: {
            ...categoryMap[key],
            subCategories: Array.from(categoryMap[key].subCategories).sort(),
          },
        }), {});

        setCategories(sortedCategories);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <Box sx={{ maxWidth: 1140, mx: 'auto', my: 2 }}>
      <Grid container spacing={2}>
        {Object.keys(categories).map((categoryName) => (
          <Grid item xs={12} sm={6} md={4} key={categoryName}>
            <Box>
              <Typography 
                variant="h3" 
                sx={{ 
                  textTransform: 'uppercase', 
                  fontSize: '18px', 
                  fontWeight: 'bold', 
                  marginBottom: '6px', 
                  lineHeight: 1.7,
                  color: '#333' // changed color for better visibility
                }}
              >
                {/* Use react-router-dom's Link for SPA navigation */}
                <Link 
                  to={`/books?category=${encodeURIComponent(categoryName)}`} 
                  style={{ textDecoration: 'none', color: 'inherit' }} // use 'inherit' to take color from the parent element
                >
                  {categoryName}
                </Link>
              </Typography>
              <List dense>
                {Array.from(categories[categoryName].subCategories).map((subCategory, index) => (
                  <ListItem 
                    key={index} 
                    sx={{ 
                      fontColor: '#888',
                      textDecoration: 'underline',
                    }}
                  >
                    {subCategory}
                  </ListItem>
                ))}
              </List>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default CategoriesPage;
