import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const query = useQuery();
  const categoryName = query.get('category');

  useEffect(() => {
    if (!categoryName) {
      setIsLoading(false);
      setError('No category specified');
      return;
    }

    setIsLoading(true);
    fetch(`http://127.0.0.1:5000/api/images?category=${encodeURIComponent(categoryName)}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`Network response was not ok, status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setBooks(data);
        setIsLoading(false);
      })
      .catch(err => {
        console.error('Error fetching books:', err);
        setError(err.message);
        setIsLoading(false);
      });
  }, [categoryName]);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Styles
  const containerStyle = {
    maxWidth: '1106px',
    padding: '16px',
    margin: 'auto',
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    border: '1px solid gray',
  };

  const rowStyle = {
    borderBottom: '1px solid gray',
  };

  const cellStyle = {
    padding: '10px',
    textAlign: 'left',
    verticalAlign: 'top',
  };

  const detailCellStyle = {
    padding: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-end',
    borderTop: '1px solid gray',
  };

  return (
    <div style={containerStyle}>
      <h1>Books in Category: {categoryName}</h1>
      <table style={tableStyle}>
        <tbody>
          {books.map((book, index) => (
            <tr key={index} style={rowStyle}>
              <td style={cellStyle} colSpan="3">
                <div style={{ display: 'flex' }}>
                  <div style={{ marginRight: '20px' }}>
                    <img
                      src={book.img_url} // Ensure each book has an img_url property
                      alt={book.title || 'No image available'}
                      style={{ width: '100px', height: 'auto' }}
                    />
                  </div>
                  <div style={{ flex: '1' }}>
                    <div><strong>{book.title || 'Untitled'}</strong></div>
                    <div>{book.publisher || 'Unknown Publisher'}</div>
                    <div>{book.authors.join(', ') || 'Unknown Authors'}</div>
                    <div style={detailCellStyle}>
                      <span>Year: {book.year || 'Unknown'}</span>
                      <span>Language: {book.language || 'Unknown'}</span>
                      <span>File: {book.file || 'Unknown'}</span>
                      <span>{book.size || 'Unknown Size'}</span>
                    </div>
                  </div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BooksPage;
