import React, { useState, useEffect } from 'react';
import { Grid, Box, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { Link } from 'react-router-dom';

const CategoriesPage = () => {
  const [categories, setCategories] = useState({});

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/images')
      .then(response => response.json())
      .then(data => {
        const categoryMap = data.reduce((acc, item) => {
          const { category, subCategory, image, title } = item;
          if (category) {
            if (!acc[category]) {
              acc[category] = { subCategories: new Set(), books: [] };
            }
            if (subCategory) {
              acc[category].subCategories.add(subCategory);
            }
            if (image && title) { // Ensure image and title are present
              acc[category].books.push({ image, title }); // Add the book image and title to the category
            }
          }
          return acc;
        }, {});

        const sortedCategories = Object.keys(categoryMap).sort().reduce((sortedAcc, key) => ({
          ...sortedAcc,
          [key]: {
            ...categoryMap[key],
            subCategories: Array.from(categoryMap[key].subCategories).sort(),
          },
        }), {});

        setCategories(sortedCategories);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Function to handle when a category is clicked
  const handleCategoryClick = (categoryName) => {
    // Here you would handle navigating to the page with the book details or set state to show the books in this component
    console.log("Category clicked:", categoryName);
  };

  return (
    <Box sx={{ maxWidth: 1140, mx: 'auto', my: 2 }}>
      <Grid container spacing={2}>
        {Object.keys(categories).map((categoryName) => (
          <Grid item xs={12} sm={6} md={4} key={categoryName}>
            <Box onClick={() => handleCategoryClick(categoryName)}>
              <Typography variant="h3" sx={{ textTransform: 'uppercase', fontSize: '18px', fontWeight: 'bold', marginTop: '40px' }}>
                {categoryName}
              </Typography>
              <TableContainer component={Paper}>
                <Table aria-label="simple table">
                  <TableHead>
                    <TableRow>
                      <TableCell>Image</TableCell>
                      <TableCell>Name</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {categories[categoryName].books.map((book, index) => (
                      <TableRow key={index}>
                        <TableCell component="th" scope="row">
                          <img src={book.image} alt={book.title} style={{ width: '50px', height: 'auto' }} />
                        </TableCell>
                        <TableCell>{book.title}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default CategoriesPage;
